$('.search-panel .dropdown-menu').find('a').click(function(e) {
		e.preventDefault();
		var param = $(this).attr("href").replace("#","");
		var concept = $(this).text();
		$('.search-panel span#search_concept').text(concept);
		$('.input-group #search_param').val(param);
});

function camptree() {
	
	$('.tree li:has(ul)').addClass('parent_li').find(' > span ').attr('title', 'Collapse');
	$('.tree div.supporter_list_tree li:has(ul)').removeClass('parent_li');
	$('.tree li.supportLI > span').on('click', function (e) {
        var children = $(this).parent('li.supportLI').find(' > ul > li');
        if (children.is(":visible")) {
            children.hide('fast');
            e.stopImmediatePropagation();
        } else {
            children.show('fast');
			e.stopImmediatePropagation();
        }
				
        e.stopPropagation();
    });
    $('.tree li.parent_li > span').on('click', function (e) {
        var children = $(this).parent('li.parent_li').find(' > ul > li');
        if (children.is(":visible")) {
            children.hide('fast');
            $(this).attr('title', 'Expand this branch').find(' > i').addClass('fa-arrow-right').removeClass('fa-arrow-down');
			e.stopImmediatePropagation();
        } else {
            children.show('fast');
            $(this).attr('title', 'Collapse this branch').find(' > i').addClass('fa-arrow-down').removeClass('fa-arrow-right');
			e.stopImmediatePropagation();
        }
				
        e.stopPropagation();
    });
    $('.tree').find('li.supportLI').find(' > ul > li').hide('fast');
    $('.tree').find('li.parent_li').find(' > ul > li').hide('fast');
	$('.treeview').find('li.parent_li').find(' > ul > li').show('fast');
	
}

$('document').ready(function () { 
    
	camptree();
	
    
});
